import React, {Fragment, useEffect, useState} from 'react';
import axios from 'axios';
import MaterialDatatable from 'material-datatable';

const MyComponent = () => {
    const [nombre, setNombre] = useState("")
    const [apellido, setApellido] = useState("")
    const [personas, setPersonas] = useState([])

    const handleInputChangeNombre = (event) => {
        setNombre(event.target.value)
    }

    const handleInputChangeApellido = (event) => {
        setApellido(event.target.value)
    }

    const enviarDatos = () => {
        console.log(`Enviando datos nombre:${nombre} y apellido:${apellido}`)

        guardarPersona();
    }

    useEffect(() => {
        getPersonas()
    },[])

    async function getPersonas(){
        try{
            const response = await axios.get('http://192.99.144.232:5000/api/personas?grupo=2')
            if (response.status == 200){
                setPersonas(personas => [...personas, response.data])
                console.log(response.data)
            }
        } catch (error){
            console.error(error)
        }
    }

    function guardarPersona(){
        axios.post('http://192.99.144.232:5000/api/personas', {
            nombre: nombre,
            apellido: apellido,
            grupo: 2 // ver que grupo somos
        })
        .then(function (response){
            if(response.status == 200){
                alert("Registro correcto")
                getPersonas()
            }
            else {
                alert("Error al guardar")
            }

        })
        .catch(function (error){
            console.log(error);
        });
    }

    const columns = [
        {
            name: "ID",
            field: id,
            options: {
                filter: true,
                sort: true,
            }
        },
        {
            name: "Nombre",
            field: nombre,
            options: {
                filter: true,
                sort: false,
            }
        },
        {
            name: "Apellido",
            field: apellido,
            options: {
                filter: true,
                sort: false,
            }
        },
        {
            name: "Grupo",
            field: grupo,
            options: {
                filter: true,
                sort: false,
            }
        },
    ];

    /*
    const data = [
        {name: "Name 1", title: "Title 1", location: "Location 1", age: 30, salary: 10},
        {name: "Name 2", title: "Title 2", location: "Location 2", age: 31, salary: 11},
    ];
    */

    const handleRowClick = (rowData, rowMeta) => {
        console.log(rowData.name)
    };

    const options = {
        filterType: 'checkbox',
        onlyOneRowCanBeSelected: true,
        onRowClick: handleRowClick
    };

    return (
        <Fragment>
            <h1>FOrmulario</h1>
            <div>
                <div>
                    <input type="text" placeholder="Nombre" name="nombre" onChange={handleInputChangeNombre} value={nombre}></input>
                </div>
                <div>
                    <input type="text" placeholder="Apellido" name="apellido" onChange={handleInputChangeApellido} value={apellido}></input>
                </div>
                <button onClick={enviarDatos}>Enviar</button>
            </div>
            <MaterialDatatable
            title={"Employee List"}
            data={personas}
            columns={columns}
            options={options}
            />
        </Fragment>
    )
}
export default MyComponent
